INSERT INTO `SERVMASK_PREFIX_wc_admin_notes` VALUES (49,'woocommerce-shipping-fedex-api-outage-2023-07-16','warning','en_US','Scheduled FedEx API outage — July 2023','On July 16 there will be a full outage of the FedEx API from 04:00 to 08:00 AM UTC. Due to planned maintenance by FedEx, you\'ll be unable to provide FedEx shipping rates during this time. Follow the link below for more information and recommendations on how to minimize impact.','{}','pending','woocommerce.com','2024-03-18 15:28:06',NULL,0,'plain','',0,0,'info');
INSERT INTO `SERVMASK_PREFIX_wc_admin_notes` VALUES (50,'wcship-2023-07-hazmat-update-needed','update','en_US','Action required: USPS HAZMAT compliance update for WooCommerce Shipping & Tax extension','<strong>Your store requires an update for the WooCommerce Shipping extension</strong>. Please update to the latest version of the WooCommerce Shipping &amp; Tax extension immediately to ensure compliance with new USPS HAZMAT rules currently in effect.','{}','pending','woocommerce.com','2024-03-18 15:28:06',NULL,0,'plain','',0,0,'info');
INSERT INTO `SERVMASK_PREFIX_wc_admin_notes` VALUES (51,'woocommerce-WCStripe-Aug-2023-update-needed','update','en_US','Action required: Security update for WooCommerce Stripe plugin','<strong>Your shop requires an important security update for the  WooCommerce Stripe plugin</strong>. The fix for this vulnerability was released on July 31. Please update immediately.','{}','pending','woocommerce.com','2024-03-18 15:28:06',NULL,0,'plain','',0,0,'info');
INSERT INTO `SERVMASK_PREFIX_wc_admin_notes` VALUES (52,'woocommerce-WCStripe-Aug-2023-security-updated','update','en_US','Security update of WooCommerce Stripe plugin','<strong>Your store has been updated to the latest secure version of the WooCommerce Stripe plugin</strong>. This update was released on July 31.','{}','pending','woocommerce.com','2024-03-18 15:28:06',NULL,0,'plain','',0,0,'info');
INSERT INTO `SERVMASK_PREFIX_wc_admin_notes` VALUES (53,'woocommerce-WooPayments-Aug-2023-update-needed','update','en_US','Action required: Security update for WooPayments (WooCommerce Payments) plugin','<strong>Your shop requires an important security update for the WooPayments (WooCommerce Payments) extension</strong>. The fix for this vulnerability was released on July 31. Please update immediately.','{}','pending','woocommerce.com','2024-03-18 15:28:06',NULL,0,'plain','',0,0,'info');
INSERT INTO `SERVMASK_PREFIX_wc_admin_notes` VALUES (54,'woocommerce-WooPayments-Aug-2023-security-updated','update','en_US','Security update of WooPayments (WooCommerce Payments) plugin','<strong>Your store has been updated to the more secure version of WooPayments (WooCommerce Payments)</strong>. This update was released on July 31.','{}','pending','woocommerce.com','2024-03-18 15:28:06',NULL,0,'plain','',0,0,'info');
INSERT INTO `SERVMASK_PREFIX_wc_admin_notes` VALUES (55,'avalara_q3-2023_noAvaTax','marketing','en_US','Automatically calculate VAT in real time','Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.','{}','pending','woocommerce.com','2024-03-18 15:28:06',NULL,0,'plain','',0,0,'info');
INSERT INTO `SERVMASK_PREFIX_wc_admin_notes` VALUES (56,'woo-activation-survey-blockers-22AUG23','info','en_US','How can we help you get that first sale?','Your feedback is vital. Please take a minute to share your experience of setting up your new store and whether anything is preventing you from making those first few sales. Together, we can make Woo even better!','{}','pending','woocommerce.com','2024-03-18 15:28:06',NULL,0,'plain','',0,0,'info');
INSERT INTO `SERVMASK_PREFIX_wc_admin_notes` VALUES (57,'woocommerce-usermeta-Sept2023-productvendors','update','en_US','Your store requires a security update','<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.','{}','pending','woocommerce.com','2024-03-18 15:28:06',NULL,0,'plain','',0,0,'info');
INSERT INTO `SERVMASK_PREFIX_wc_admin_notes` VALUES (58,'woocommerce-STRIPE-Oct-2023-update-needed','update','en_US','Action required: Security update for WooCommerce Stripe Gateway','<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.','{}','pending','woocommerce.com','2024-03-18 15:28:06',NULL,0,'plain','',0,0,'info');
INSERT INTO `SERVMASK_PREFIX_wc_admin_notes` VALUES (59,'amazon-mcf-reviews-2023-12-07','marketing','en_US','Enjoying Amazon MCF for WooCommerce?','We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!','{}','pending','woocommerce.com','2024-03-18 15:28:06',NULL,0,'plain','',0,0,'info');
INSERT INTO `SERVMASK_PREFIX_wc_admin_notes` VALUES (60,'amazon_pay_reactivate_q1-24','marketing','en_US','Special offer: Activate 0% promo rate for Amazon Pay','For a limited time, Woo merchants who register with Amazon Pay can enjoy a promotional rate of 0% on all transaction fees for 30 days. Valid for businesses based in the United Kingdom. Terms and conditions apply.','{}','pending','woocommerce.com','2024-03-18 15:28:06',NULL,0,'plain','',0,0,'info');
INSERT INTO `SERVMASK_PREFIX_wc_admin_notes` VALUES (61,'remove_estimated_deposits_2024','marketing','en_US','Estimated deposits are going away','To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.','{}','pending','woocommerce.com','2024-03-18 15:28:06',NULL,0,'plain','',0,0,'info');
INSERT INTO `SERVMASK_PREFIX_wc_admin_notes` VALUES (62,'wc_admin_UKpricingchange_WCPay_Q1_2024','marketing','en_US','Fee Changes for WooPayments in the EEA and UK','WooPayments fees for transactions between the EEA and UK will change as of 1st March, 2024. Transactions between the UK and EEA are now deemed by card networks to be cross-border, thus international payment fees will be applicable. This also changes base processing fees.','{}','pending','woocommerce.com','2024-03-18 15:28:06',NULL,0,'plain','',0,0,'info');
INSERT INTO `SERVMASK_PREFIX_wc_admin_notes` VALUES (63,'airwallex_q1_2024','marketing','en_US','Save big on foreign exchange (FX) fees','Boost your international sales by offering Apple Pay, Google Pay, and 60+ local payment methods with Airwallex. Save time and money by settling payments directly in USD, EUR, HKD, SGD, AUD, and more — without the extra FX fees. Simple, cost-effective, fast.','{}','pending','woocommerce.com','2024-03-18 15:28:06',NULL,0,'plain','',0,0,'info');
INSERT INTO `SERVMASK_PREFIX_wc_admin_notes` VALUES (64,'wc-admin-wc-helper-connection','info','en_US','Connect to Woo.com','Connect to get important product notifications and updates.','{}','unactioned','woocommerce-admin','2024-03-18 15:28:09',NULL,0,'plain','',0,0,'info');
